def _merge_item(rgInv, rgDesc):
    for key, value in rgInv.items():
        rgDesc[key] = value
    return rgDesc

def merge_items(rgInv, rgDesc):
    """
    Merge the data from rgInventory and rgDescriptions into one list
    :param rgInv: :class dict:
    :param rgDesc: :class dict:
    :return inventory: :class list:
    """
    new_items = []
    for item_ids in rgInv.values():
        item_id = item_ids['classid'] + '_' + item_ids['instanceid']
        new_items.append(_merge_item(item_ids, rgDesc[item_id]))
    return new_items

